import {
  View,
  Image,
  Text,
  ActivityIndicator,
  TextInput,
  Button,
  Pressable,
} from "react-native";
import { useState } from "react";

import { updateProfile } from "firebase/auth";

import { auth } from "../firebase";

export function LoadingScreen(userdetails) {
  const [name, setName] = useState("");
  console.log("loading");
  const handleName = async (name) => {
    updateProfile(auth.currentUser, {
      displayName: name,
    })
      .then(() => {
        console.log("updated with " + name);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  if (auth.currentUser.displayName == null) {
    return (
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <Text>How should we greet you?</Text>
        <TextInput
          placeholder={"Name"}
          value={name}
          onChangeText={setName}
          style={{ flex: 1, paddingVertical: 0, paddingHorizontal: 20 }}
        />
        <Pressable onPress={() => handleName(name)}>
          <Text>Confim</Text>
        </Pressable>
      </View>
    );
  }
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Text>Roasting Coffee...</Text>
      <ActivityIndicator size="large" />
    </View>
  );
}
