import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, Image, Button, Pressable } from "react-native";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { Dimensions } from "react-native";
import Feather from "react-native-vector-icons/Feather";
import * as React from "react";
import { NavigationContainer, useIsFocused } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import CardIcon from "./assets/coffeecard(1).ico";
const Stack = createNativeStackNavigator();

function MainScreen({ navigation }) {
  const name = "Julian";
  const isFocused = useIsFocused();
  let greetings = ["Hello", "Welcome back", "Hey ðŸ‘‹", "Good Morning"];
  let randomGreeting = greetings[Math.floor(Math.random() * greetings.length)];
  return (
    <View style={styles.maincontainer}>
      <View style={styles.greetings}>
        <Text style={{ fontSize: 36, fontWeight: "normal" }}>
          {randomGreeting},{"\n"}
          <Text style={{ fontWeight: "bold" }}>{name}</Text>
        </Text>
        <FontAwesome.Button
          name="user-circle"
          backgroundColor="0"
          size={50}
        ></FontAwesome.Button>
      </View>
      <CoffeeSaved />
      <View style={styles.qr}>
        <Image
          source={require("./assets/QR_code_for_mobile_English_Wikipedia.svg")}
          style={{
            height: "100%",
            width: "80%",
            resizeMode: "contain",
            borderRadius: 30,
            borderWidth: 5,
            backgroundColor: "#d3d3d3",
          }}
        />
      </View>
      <NavBar navigation={navigation} />
    </View>
  );
}

function Card({ navigation }) {
  /* List of loyality cards customer has  
cards = [{name: "store name", points: "points", pointsTotat:"total" , image: "image", stamp: "stamp"}] 
*/
  let cards = [
    {
      name: "store name",
      storelogo: "img",
      points: "points",
      pointsTotal: "total",
      image: "image",
      stamp: "stamp",
      address: "address",
    },
  ];
  return (
    <View>
      {cards.map((card) => (
        <View style={styles.cards}>
          <Image source={require(card.storelogo)} />
          <Text style={styles.cardname}>{card.name}</Text>
          <Text style={styles.cardpoints}>
            {card.points}/{card.pointsTotal}
          </Text>
        </View>
      ))}
    </View>
  );
}

function CardScreen({ navigation, route }) {
  return (
    <View>
      <Text>card Screen</Text>
      <NavBar navigation={navigation} />
    </View>
  );
}
function StoresScreen({ navigation }) {
  return (
    <View>
      <Text>shop Screen</Text>
      <NavBar navigation={navigation} />
    </View>
  );
}
function SettingsScreen() {
  return (
    <View>
      <Text>settings Screen</Text>
    </View>
  );
}

function NavBar({ navigation }) {
  let isFocused = false;
  return (
    <View style={styles.navbar}>
      <Pressable
        style={isFocused ? { backgroundColor: "0" } : styles.activeNav}
        onPress={() => navigation.navigate("card")}
      >
        <Image
          source={require("./assets/coffeecard(1).ico")}
          style={{
            width: 50,
            height: 50,
            resizeMode: "contain",
            backgroundColor: "0",
          }}
          onPress={() => navigation.navigate("Home")}
        ></Image>
      </Pressable>

      <MaterialIcons.Button
        name="qr-code"
        onPress={() => navigation.navigate("Home")}
        backgroundColor="0"
        size={50}
      ></MaterialIcons.Button>

      <MaterialIcons.Button
        name="storefront"
        onPress={() => navigation.navigate("stores")}
        backgroundColor="0"
        size={50}
      ></MaterialIcons.Button>
    </View>
  );
}

function CoffeeSaved() {
  let coffeeSaves = 1;
  return (
    <View style={styles.coffeesaved}>
      <Text style={{ fontSize: 25, paddingLeft: 20, fontWeight: "bold" }}>
        Free Coffees{" "}
      </Text>
      <View>
        <Image
          source={require("./assets/3dCup.png")}
          style={{
            width: 120,
            height: 120,
            borderWidth: 5,
            borderRadius: 40,
          }}
        />
        <Text
          style={{
            fontSize: 30,
            fontWeight: "bold",
            position: "absolute",
            top: "40%",
            alignSelf: "center",
          }}
        >
          {coffeeSaves}
        </Text>
      </View>
    </View>
  );
}

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen
          name="Home"
          component={MainScreen}
          options={{
            headerStyle: {
              opacity: 0,
              height: 0,
            },
            animation: "slide_from_left",
          }}
        />
        <Stack.Screen
          name="card"
          component={CardScreen}
          options={{
            headerStyle: {},
            animation: "slide_from_left",
          }}
        />
        <Stack.Screen
          name="stores"
          component={StoresScreen}
          options={{
            headerStyle: {
              opacity: 0,
              height: 0,
            },
            animation: "slide_from_left",
          }}
        />
        <Stack.Screen
          name="settings"
          component={SettingsScreen}
          options={{
            headerStyle: {
              opacity: 0,
              height: 0,
            },
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    height: Dimensions.get("window").height,
    width: "100%",
    backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 30,
  },

  image: {
    width: 100,
    height: 100,
    borderRadius: 18,
  },
  icon: {
    width: 40,
    height: 40,
  },
  greetings: {
    flex: 1.5,
    backgroundColor: "#d3d3d3",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
    alignItems: "flex-end",
    padding: 40,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: "#d3d3d3",
    borderRadius: 44,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    marginHorizontal: 50,
  },
  qr: {
    flex: 3,

    width: "100%",

    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
  },
  navbar: {
    backgroundColor: "#d3d3d3",
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "100%",
    borderTopLeftRadius: 40,
    borderTopRightRadius: 40,

    // borderRadius: 40,
    // margin: 10,
  },
  button: {
    borderRadius: 50,
    backgroundColor: "0",
    borderWidth: 2,
    justifyContent: "center",
    alignSelf: "center",
  },
  activeNav: {
    borderRadius: 20,
    borderWidth: 5,
    backgroundColor: "brown",
    padding: 10,
    marginBottom: 40,
  },
});
