import * as React from "react";
import {
  View,
  TextInput,
  Button,
  Text,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../firebase";

const handleEmailSignUp = (email, password) => {
  createUserWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      // Signed up
      const user = userCredential.user;
      console.log(user);
      // ...
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      console.log(errorCode + errorMessage);
    });
};

export default function SignUpScreen() {
  const [password, setPassword] = React.useState("");
  const [firstname, setFirstmame] = React.useState("");
  const [lastname, setLastname] = React.useState("");
  const [email, setEmail] = React.useState("");

  return (
    <View>
      <Text>Sign Up with</Text>
      <TouchableOpacity
        onPress={() => {}}
        style={{
          borderColor: "#ddd",
          borderWidth: 2,
          borderRadius: 10,
          paddingHorizontal: 30,
          paddingVertical: 10,
        }}
      >
        <FontAwesome name="google" height={24} width={24} />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {}}
        style={{
          borderColor: "#ddd",
          borderWidth: 2,
          borderRadius: 10,
          paddingHorizontal: 30,
          paddingVertical: 10,
        }}
      >
        <FontAwesome name="facebook-square" height={24} width={24} />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {}}
        style={{
          borderColor: "#ddd",
          borderWidth: 2,
          borderRadius: 10,
          paddingHorizontal: 30,
          paddingVertical: 10,
        }}
      >
        <FontAwesome name="twitter" height={24} width={24} />
      </TouchableOpacity>
      <Text>Or Create account with Email</Text>
      <TextInput style={styles.input} placeholder="First Name"></TextInput>
      <TextInput style={styles.input} placeholder="Last Name"></TextInput>
      <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        placeholder="Email"
      ></TextInput>
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      ></TextInput>
      <TextInput
        style={styles.input}
        placeholder="Confirm Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      ></TextInput>
      <Button
        title="Create Account"
        onPress={handleEmailSignUp(email, password)}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  input: {},
});
