import {
  StyleSheet,
  View,
  Image,
  Text,
  ImageBackground,
  Button,
} from "react-native";
import { Dimensions } from "react-native";
import { useIsFocused } from "@react-navigation/native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import NavBar from "../components/NavBar";
import { getAuth, signOut } from "firebase/auth";
import app from "../firebase";
import { useState, useEffect } from "react";
import { CoffeeIconSVG, Background } from "../assets/socialSVG.js";
import QRCode from "react-native-qrcode-svg";
import { useFonts, TitanOne_400Regular } from "@expo-google-fonts/titan-one";

function UserButton() {
  const [showMenu, setShowMenu] = useState(false);
  const auth = getAuth(app);
  const handleClick = () => {
    console.log("clicked");
    signOut(auth)
      .then(() => {
        // Sign-out successful.
        console.log("Sign-out successful.");
      })
      .catch((error) => {
        // An error happened.
        console.log("sign out error" + error);
      });
  };

  return (
    <>
      <FontAwesome.Button
        name="user-circle"
        backgroundColor="0"
        size={50}
        onPress={() => setShowMenu(!showMenu)}
      ></FontAwesome.Button>
      {showMenu ? (
        <View>
          <Button title="Edit Profile" />
          <Button title="Log Out" onPress={handleClick} />
        </View>
      ) : null}
    </>
  );
}
function SplashScreen() {
  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Image source={require("../assets/3dCup.png")} width={50} height={50} />
      <Text>Roasting Coffee...</Text>
      <ActivityIndicator size="large" />
    </View>
  );
}
export default function Home({ navigation, route }) {
  console.log("Home Page");
  const userid = route.params;
  const [userDetails, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const isFocused = useIsFocused();
  let greetings = ["Hello", "Welcome back", "Hey ðŸ‘‹", "Good Morning"];
  let randomGreeting = greetings[Math.floor(Math.random() * greetings.length)];
  let [fontsLoaded] = useFonts({
    TitanOne_400Regular,
  });

  useEffect(() => {
    async function fetchFirebaseInfo() {
      if (userid !== null) {
        // const usercards = await getUserCards();
        // setUserCards(usercards);
        const userinfo = await getUserInfo();
        setUserInfo(userinfo);
      }
    }
    fetchFirebaseInfo();
  }, [userToken]);

  if (!fontsLoaded) {
    return null;
  }
  if (userDetails !== null) {
    let name = userDetails.name.substring(0, userDetails.name.indexOf(" "));
    name = name.charAt(0).toUpperCase() + name.slice(1);
    setLoading(false);
  }
  if (loading) {
    return <SplashScreen />;
  }
  return (
    <View style={styles.maincontainer}>
      <ImageBackground
        source={require("../assets/background.png")}
        resizeMode="cover"
        style={styles.image}
      >
        <View style={styles.greetings}>
          <Text
            style={{
              fontSize: 40,

              fontFamily: "TitanOne_400Regular",
            }}
          >
            {randomGreeting},{"\n"}
            <Text style={{ fontWeight: "bold" }}>{name}</Text>
          </Text>
          <UserButton />
        </View>
        {/* <CoffeeSaved coffeenumber={userDetails.freeCoffees} /> */}
        <CoffeeSaved coffeenumber={userDetails.coffee_earnt} />
        <View style={styles.qr}>
          <QRCode
            value={userDetails.userId}
            size={250}
            backgroundColor={"transparent"}
            logo={require("../assets/coffeeGuy.png")}
            logoSize={150}
          />
        </View>
        <NavBar navigation={navigation} isFocused={isFocused ? "main" : null} />
      </ImageBackground>
    </View>
  );
}

function CoffeeSaved({ coffeenumber }) {
  return (
    <View style={styles.coffeesaved}>
      <Text
        style={{
          fontSize: 25,
          paddingLeft: 20,
          fontWeight: "bold",
          textAlign: "center",
        }}
      >
        Free Coffees
      </Text>
      <View>
        <Image
          source={require("../assets/3dCup.png")}
          style={{
            width: 120,
            height: 120,
            borderRadius: 40,
          }}
        />
        <Text
          style={{
            fontSize: 30,
            fontWeight: "bold",
            position: "absolute",
            top: "40%",
            alignSelf: "center",
          }}
        >
          {coffeenumber}
        </Text>
      </View>
    </View>
  );
}
const colors = {
  bg: "#fff8e7",
  widgetbg: "#cdb891",
};

const styles = StyleSheet.create({
  image: {
    flex: 1,
    justifyContent: "center",
    minHeight: Dimensions.get("window").height,
    maxHeight: Dimensions.get("window").height,
    width: "100%",
    flexDirection: "column",
    rowGap: 50,
    justifyContent: "space-between",
    overflow: "hidden",
  },
  maincontainer: {
    minHeight: Dimensions.get("window").height,
    maxHeight: Dimensions.get("window").height,
    width: "100%",
    // backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 50,
    justifyContent: "space-between",
    overflow: "hidden",
    backgroundColor: colors.bg,
  },
  greetings: {
    flex: 1.25,
    backgroundColor: colors.widgetbg,
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
    alignItems: "flex-end",
    padding: 40,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: colors.widgetbg,
    borderRadius: 44,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "65%",
    alignSelf: "center",
  },
  qr: {
    flex: 3,
    width: "auto",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    borderRadius: 30,
    borderWidth: 10,
    backgroundColor: colors.widgetbg,
    padding: 30,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: colors.widgetbg,
    borderRadius: 44,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "65%",
    alignSelf: "center",
  },
});
