import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, Image, Button } from "react-native";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { Dimensions } from "react-native";
import Feather from "react-native-vector-icons/Feather";
import * as React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

const Stack = createNativeStackNavigator();

function MainScreen({ navigation }) {
  return (
    <View>
      <Text>Main Screen</Text>
    </View>
  );
}

function CardScreen({ navigation, route }) {
  return (
    <View>
      <Text>card Screen</Text>
    </View>
  );
}
function StoresScreen() {
  return (
    <View>
      <Text>shop Screen</Text>
    </View>
  );
}
function SettingsScreen() {
  return (
    <View>
      <Text>settings Screen</Text>
    </View>
  );
}

function NavBar({ navigation }) {
  return (
    <View style={styles.navbar}>
      <Button style={{ width: 40, height: 40 }}>
        <Image
          source={require("./assets/card_icon.png")}
          style={styles.icon}
          onPress={() => navigation.navigate("Home")}
        ></Image>
      </Button>

      <MaterialIcons.Button
        name="qr-code"
        onPress={() => navigation.navigate("Home")}
      ></MaterialIcons.Button>

      <MaterialIcons.Button
        name="storefront"
        onPress={() => navigation.navigate("Home")}
      ></MaterialIcons.Button>
      {/* <FontAwesome.Button name="user-circle"></FontAwesome.Button> */}
    </View>
  );
}

function CoffeeSaved() {
  let coffeeSaves = 1;
  let cashSave = coffeeSaves * 4.5;
  return (
    <View style={styles.coffeesaved}>
      <Text>Your Free Coffee Counter</Text>
      <Text>
        {coffeeSaves}
        <Feather name="coffee" style={styles.icon}></Feather>
      </Text>
      {/* <Text>{`$${cashSave}`}</Text> */}
    </View>
  );
}

export default function App() {
  const name = "Julian Lund";
  const MyStack = () => {
    return (
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={MainScreen} />
        <Stack.Screen name="card" component={CardScreen} />
        <Stack.Screen name="stores" component={StoresScreen} />
        <Stack.Screen name="settings" component={SettingsScreen} />
      </Stack.Navigator>
    );
  };
  return (
    <NavigationContainer>
      <MyStack />
      <View style={styles.maincontainer}>
        <View style={styles.greetings}>
          <Text>Hello,</Text>
          <Text>{name}</Text>
          <Image source={require("./assets/coffee.png")} style={styles.image} />
        </View>
        <StatusBar style="auto" />
        <CoffeeSaved />
        <View style={styles.qr}>
          <Image
            source={require("./assets/QR_code_for_mobile_English_Wikipedia.svg")}
            style={{ height: "90%", width: "100%", resizeMode: "contain" }}
          />
        </View>
        <NavBar />
      </View>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    height: Dimensions.get("window").height,
    width: "100%",
    backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 10,
  },

  image: {
    width: 100,
    height: 100,
    borderRadius: 18,
  },
  icon: {
    width: 40,
    height: 40,
  },
  greetings: {
    flex: 2,
    backgroundColor: "red",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: "green",
    borderRadius: 44,
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 50,
    marginVertical: 15,
  },
  qr: {
    flex: 2,
    borderRadius: 30,
    width: "50%",
    backgroundColor: "orange",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
  },
  navbar: {
    backgroundColor: "blue",
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "auto",
    borderRadius: 44,
    marginHorizontal: 20,
    marginVertical: 10,
  },
});
