import {
  createUserWithEmailAndPassword,
  getAuth,
  updateProfile,
  TwitterAuthProvider,
  FacebookAuthProvider,
  GoogleAuthProvider,
  signInWithPopup,
} from "firebase/auth";
import { app } from "../firebase";
import { FacebookSVG, TwitterSVG, GoogleSVG } from "../assets/socialSVG";
import { AddUser } from "../firebasefunctions";
import {
  View,
  TextInput,
  Button,
  Text,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Image,
} from "react-native";

const HandleSocialLogin = ({ provider, name }) => {
  const auth = getAuth(app);
  auth.languageCode = "it";
  console.log("Logging in " + name);
  console.log(provider);
  signInWithPopup(auth, provider)
    .then((result) => {
      const credential = provider.credentialFromResult(result);
      const token = credential.accessToken;
      // The signed-in user info.
      const user = result.user;
      //   AddUser({
      //     created_at: new Date(),
      //     userID: user.uid,
      //     name: user.displayName,
      //     email: user.email,
      //     earnt_coffees: 0,
      //   });

      console.log("Logged in with:", user.email);
    })
    .catch((error) => {
      // Handle Errors here.
      const errorCode = error.code;
      const errorMessage = error.message;
      // The email of the user's account used.
      const email = error.customData.email;
      // The AuthCredential type that was used.
      const credential = GoogleAuthProvider.credentialFromError(error);
      console.log(errorCode, errorMessage, email, credential);
      // ...
    });
};

function SocialButton({ provider, SVG, name }) {
  return (
    <TouchableOpacity
      onPress={() => HandleSocialLogin(provider, name)}
      style={{
        borderColor: "#ddd",
        borderWidth: 2,
        borderRadius: 10,
        paddingHorizontal: 30,
        paddingVertical: 10,
      }}
    >
      <SVG height={24} width={24} />
    </TouchableOpacity>
  );
}

export function SocialButtons() {
  const GoogleProvider = new GoogleAuthProvider();
  const TwitterProvider = new TwitterAuthProvider();
  const FacebookProvider = new FacebookAuthProvider();
  return (
    <>
      <SocialButton provider={GoogleProvider} SVG={GoogleSVG} name="Google" />
      <SocialButton
        provider={FacebookProvider}
        SVG={FacebookSVG}
        name="Facebook"
      />
      <SocialButton
        provider={TwitterProvider}
        SVG={TwitterSVG}
        name="Twitter"
      />
    </>
  );
}
