import * as React from "react";
import { View, TextInput, Button, Text, TouchableOpacity } from "react-native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { auth } from "../firebase";

import {
  signInWithEmailAndPassword,
  TwitterAuthProvider,
  FacebookAuthProvider,
  GoogleAuthProvider,
} from "firebase/auth";

const GoogleProvider = new GoogleAuthProvider();
const TwitterProvider = new TwitterAuthProvider();
const FacebookProvider = new FacebookAuthProvider();

export default function SignInScreen({ navigation }) {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleEmailandPasswordLogin = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredentials) => {
        const user = userCredentials.user;
        console.log("Logged in with:", user.email);
      })
      .catch((error) => alert(error.message));
  };

  const HandleGmailLogin = () => {
    console.log("Loggin in Gmail");
    auth
      .signInWithPopup(GoogleProvider)
      .then((userCredentials) => {
        const user = userCredentials.user;
        console.log("Logged in with:", user.email);
      })
      .catch((error) => alert(error.message));
  };
  const HandleFacebookLogin = () => {
    console.log("Loggin in Facebook");
    auth
      .signInWithPopup(FacebookProvider)
      .then((userCredentials) => {
        const user = userCredentials.user;
        console.log("Logged in with:", user.email);
      })
      .catch((error) => alert(error.message));
  };
  const HandleTwitterLogin = () => {
    console.log("Loggin in Twitter");
    auth
      .signInWithPopup(TwitterProvider)
      .then((userCredentials) => {
        const user = userCredentials.user;
        console.log("Logged in with:", user.email);
      })
      .catch((error) => alert(error.message));
  };
  return (
    <View>
      <FontAwesome name="at" size={20} />
      <TextInput
        placeholder={"Email ID"}
        value={email}
        onChangeText={setEmail}
      />
      <FontAwesome name="lock" size={20} />
      <TextInput
        placeholder={"Password"}
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />
      <Button title="Login" onPress={handleEmailandPasswordLogin} />
      <Text>Forgot your password?</Text>

      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          marginBottom: 30,
        }}
      >
        <Text style={{ textAlign: "center", color: "#666", marginBottom: 30 }}>
          Or, login with ...
        </Text>
        <TouchableOpacity
          onPress={() => {
            HandleGmailLogin;
          }}
          style={{
            borderColor: "#ddd",
            borderWidth: 2,
            borderRadius: 10,
            paddingHorizontal: 30,
            paddingVertical: 10,
          }}
        >
          <FontAwesome name="google" height={24} width={24} />
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            HandleFacebookLogin;
          }}
          style={{
            borderColor: "#ddd",
            borderWidth: 2,
            borderRadius: 10,
            paddingHorizontal: 30,
            paddingVertical: 10,
          }}
        >
          <FontAwesome name="facebook-square" height={24} width={24} />
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => {
            HandleTwitterLogin;
          }}
          style={{
            borderColor: "#ddd",
            borderWidth: 2,
            borderRadius: 10,
            paddingHorizontal: 30,
            paddingVertical: 10,
          }}
        >
          <FontAwesome name="twitter" height={24} width={24} />
        </TouchableOpacity>
      </View>

      <View
        style={{
          flexDirection: "row",
          justifyContent: "center",
          marginBottom: 30,
        }}
      >
        <Text>New to the app?</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Register")}>
          <Text style={{ color: "#AD40AF", fontWeight: "700" }}> Register</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
