import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, Image } from "react-native";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { Dimensions } from "react-native";

function NavBar() {
  return (
    <View style={styles.navbar}>
      <MaterialIcons.Button name="qr-code"></MaterialIcons.Button>
      <Image
        source={require("./assets/card_icon.png")}
        style={styles.icon}
      ></Image>
      <MaterialIcons.Button name="storefront"></MaterialIcons.Button>
      <FontAwesome.Button name="user-circle"></FontAwesome.Button>
    </View>
  );
}

function CoffeeSaved() {
  let coffeeSaves = 1;
  let cashSave = coffeeSaves * 4.5;
  return (
    <View style={styles.coffeesaved}>
      <Text>Coffee Saved</Text>
      <Text>{coffeeSaves}</Text>
      <Text>{`$${cashSave}`}</Text>
      <Text></Text>
    </View>
  );
}

export default function App() {
  const name = "Julian Lund";
  return (
    <View style={styles.maincontainer}>
      <View style={styles.greetings}>
        <Text>Hello,</Text>
        <Text>{name}</Text>
        <Image source={require("./assets/coffee.png")} style={styles.image} />
      </View>
      <StatusBar style="auto" />
      <CoffeeSaved />
      <View style={styles.qr}>
        <Image
          source={require("./assets/QR_code_for_mobile_English_Wikipedia.svg")}
          style={{ height: "80%", width: "100%", resizeMode: "contain" }}
        />
      </View>
      <NavBar />
    </View>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    height: Dimensions.get("window").height,
    width: "100%",
    backgroundColor: "#fff",
    flexDirection: "column",
  },

  image: {
    width: 100,
    height: 100,
    borderRadius: 18,
  },
  icon: {
    width: 40,
    height: 40,
  },
  greetings: {
    flex: 2,
    backgroundColor: "red",
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: "green",
  },
  qr: {
    flex: 2,
    backgroundColor: "orange",
    justifyContent: "center",
    alignItems: "center",
  },
  navbar: {
    backgroundColor: "blue",
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "auto",
    borderRadius: 44,
    margin: 20,
  },
});
