import {
  View,
  Image,
  Dimensions,
  StyleSheet,
  Pressable,
  Text,
} from "react-native";
import AntIcon from "react-native-vector-icons/AntDesign";

const getCoffeesEarnt = () => {
  return 1;
};

function CoffeeTicker({ list, coffeesEarnt, logo }) {
  console.log(list, coffeesEarnt);
  return (
    <View style={styles.CheckBox}>
      {coffeesEarnt === list.length ? (
        <View>
          <Image src={require("../assets/coffeeGuy.png")} />
          <Text>Free Coffee</Text>
        </View>
      ) : (
        <>
          {list.map((i) => {
            {
              console.log(i);
            }
            // <View
            //   style={{
            //     width: 50,
            //     height: 50,
            //     backgroundColor: "#FFFDD0",
            //     alignItems: "center",
            //     justifyContent: "center",
            //     borderRadius: 80,
            //     borderWidth: 4,
            //     borderColor: "#C4A484",
            //   }}
            // >
            {
              i < coffeesEarnt ? (
                <View>
                  <Image
                    source={require("../assets/starbuck_test/logo.png")}
                    style={{
                      width: 50,
                      height: 50,
                      opacity: 0.4,
                    }}
                  ></Image>
                  <Image
                    source={require("../assets/bean_stamp.png")}
                    style={{
                      width: 80,
                      height: 80,
                      position: "absolute",
                      top: "50%",
                      left: "50%",
                      transform: "translateY(-50%) translateX(-50%)",
                    }}
                  />
                </View>
              ) : (
                <View style={{ width: 50, height: 50 }}>
                  <Image
                    source={require("../assets/starbuck_test/logo.png")}
                    style={{ width: 50, height: 50, opacity: 0.4 }}
                  ></Image>
                </View>
              );
            }
            // </View>;
          })}
        </>
      )}
    </View>
  );
}

export default function LoyaltyCard({ navigation, route }) {
  console.log("LoyaltyCard");
  //   const cardDetails = route.params;
  const cardDetails = {
    name: "Starbucks",
    pointsTotal: 6,
    logo: "starbuck_test/logo.png",
    stamp: "bean_stamp.png",
    address: "10 test street perth",
  };
  const coffeesEarnt = getCoffeesEarnt();
  console.log(cardDetails);
  console.log(cardDetails.pointsTotal + " " + coffeesEarnt);
  const list = [...Array(cardDetails.pointsTotal).keys()];
  console.log(list);
  return (
    <View
      style={{
        minHeight: Dimensions.get("window").height,
        maxHeight: Dimensions.get("window").height,
        width: "100%",
        flexDirection: "column",
        justifyContent: "space-evenly",
        overflow: "hidden",
        alignItems: "center",
      }}
    >
      <Image
        source={require("../assets/" + cardDetails.logo)}
        style={{
          height: 250,
          width: 250,
          resizeMode: "contain",
          alignSelf: "center",
          flex: 1.5,
          padding: 30,
        }}
      />
      <CoffeeTicker list={list} coffeesEarnt={coffeesEarnt} />
      <Pressable
        onPress={() => navigation.navigate("card")}
        style={{ position: "absolute", left: 0, top: 0, margin: 25 }}
      >
        <AntIcon
          name="back"
          size={30}
          style={{
            borderWidth: 2,
            padding: 10,
            borderRadius: 40,
          }}
        />
      </Pressable>
    </View>
  );
}
const styles = StyleSheet.create({
  CheckBox: {
    alignContent: "space-around",
    flexDirection: "row",
    width: "80%",
    justifyContent: "center",
    flexWrap: "wrap",
    borderWidth: 4,
    gap: 40,
    padding: 20,
    paddingVertical: 40,
    alignItems: "center",
    borderRadius: 25,
    borderColor: "#4B2D0B",
    flex: 2,
    marginBottom: 40,
  },
});
