import { View, Image } from "react-native";

export default function LoyaltyCard({ navigation, route }) {
  const getCoffeesEarnt = () => {
    return 1;
  };
  const cardDetails = route.params;
  const coffeesEarnt = getCoffeesEarnt();
  console.log(cardDetails);
  console.log("LoyaltyCard");
  console.log(cardDetails.logo);
  console.log(cardDetails.stamp);
  const checked = false;
  const CoffeeCount = () => {
    const n = cardDetails.pointTotal;
    return (
      <>
        {[...Array(n)].map((e, i) => {
          <View style={{ width: 50, height: 50, backgroundColor: "cream" }}>
            {checked ? (
              <Image source={require("../assets/" + cardDetails.logo)} />
            ) : null}
          </View>;
        })}
      </>
    );
  };
  return (
    <View>
      <Image
        source={require("../assets/" + cardDetails.logo)}
        width={100}
        height={100}
      />
      <View>
        <CoffeeCount />
      </View>
    </View>
  );
}
