import { View, Image, Text, StyleSheet ,TextInput, Pressable, Dimensions} from "react-native";
import NavBar from "../components/NavBar";
import { useIsFocused } from "@react-navigation/native";
import GetLocation from 'react-native-get-location'
import { useState } from "react";
import {UserButton} from '../components/buttons'


export default function Card({ navigation }) {
  console.log("Card page")
  const isFocused = useIsFocused();
  const [searchStoreFilter, setSearchFilter] = useState("")
  //  List of loyality cards customer has  
  let cards = [{name: "Test Store",  pointsTotal:6 , logo: "3dCup.png", stamp: "coffee_stamp.jpg" , address:"10 test street perth"}] 

const navigateLoyaltyPage = (card) => {
  console.log(card)
  navigation.replace("loyaltyCard",{card})
}

  GetLocation.getCurrentPosition({
    enableHighAccuracy: true,
    timeout: 60000,
})
.then(location => {
  let userLocation = location
})
.catch(error => {
    const { code, message } = error;
    console.warn(code, message);
})


  // let cards= getCards(userID)
  return (
    <View style={styles.maincontainer}> 
      <View style={styles.greetings}>
        <Text style={{ fontSize: 36, fontWeight: "normal" }}>Your Cards</Text>
        <UserButton/>
          </View>

      <View style={styles.cardsContainer}>
      <TextInput style={{borderRadius:25,
  borderWidth: 5,
  borderColor:"#d3d3d3",
  width:"100%",
  backgroundColor:"#fff", padding:10, marginBottom:15}} placeholder="Find Store" onChangeText={newVal => setSearchFilter(newVal)}></TextInput>
      {cards.map((card) => (
        <StoreCard card={card} navigation={navigateLoyaltyPage}/>
      ))}

      </View>
      <NavBar navigation={navigation} isFocused={isFocused ? "card" : null} />
    </View>
  );
}

function StoreCard({navigation, card}){
  const getDistance = (lat1, lon1, lat2, lon2) => {
    return(0)
  }
return(
  <Pressable style={{flexDirection:"row",
  borderRadius:25,
  borderWidth: 5,
  borderColor:"#d3d3d3",
  width:"100%",
  backgroundColor:"#fff",}} onPress={()=>navigation(card)}>
          <Image style={{
    width: 60,
      height: 60,
    padding:20
          }} source={require(`../assets/${card.logo}`)} />
          <View style={{ justifyContent:"center", width:"60%"}}>
          <Text style={{fontWeight:40, fontSize:18}}>{card.name}</Text>
          <Text style={{fontSize:15}}>{card.address} </Text>
          <Text>{getDistance} Away</Text></View>
          <View>
          <Text style={{justifyContent:"center",fontSize:30, alignItems:"center" ,height:"100%"}}>
            0/{card.pointsTotal}
          </Text></View>
        </Pressable>
)
}

const styles = StyleSheet.create({
  maincontainer: {
    minHeight: Dimensions.get("window").height,
    maxHeight: Dimensions.get("window").height,
    width: "100%",
    // backgroundColor: "#fff",
