    rowGap: 50,
    justifyContent: "space-between",
  },
  cardsContainer:{
    alignSelf:"stretch",
    paddingHorizontal:30,
  },
  greetings: {
    height:"15%",
    backgroundColor: "#d3d3d3",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
