import { View, Image, Text, Pressable } from "react-native";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { StyleSheet } from "react-native";
import {
  BlackCoffeeCardIconSVG,
  CoffeeCardIconSVG,
  ColoredCoffeeCardIconSVG,
} from "../assets/socialSVG.js";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import LinearGradient from "react-native-linear-gradient";
import MaskedView from "@react-native-masked-view/masked-view";

export default function NavBar({ navigation, isFocused }) {
  return (
    <View style={styles.navbar}>
      {" "}
      <MaskedView
        style={styles.navIcon}
        maskElement={
          <View
            style={{
              backgroundColor: "transparent",
              justifyContent: "center",
              alignItems: "center",
            }}
            onPress={() => navigation.navigate("Home")}
          >
            <MaterialIcons
              name="home"
              backgroundColor="0"
              color={isFocused == "main" ? "black" : "white"}
              size={isFocused == "main" ? 70 : 50}
            />
            <LinearGradient
              colors={["#F7C650", "rgba(247, 198, 80, 0.71)"]}
              style={{ flex: 1 }}
            />
          </View>
        }
      >
        <LinearGradient
          colors={["#F7C650", "rgba(247, 198, 80, 0.71)"]}
          style={{ flex: 1 }}
        />
      </MaskedView>
      <MaterialIcons.Button
        name="home"
        onPress={() => navigation.navigate("Home")}
        backgroundColor="0"
        color={isFocused == "main" ? "black" : "white"}
        style={
          isFocused == "main" ? styles.activeNav : { backgroundColor: "0" }
        }
        size={isFocused == "main" ? 70 : 50}
      />
      {/* <MaterialCommunityIcons.Button
        name="card-multiple-outline"
        onPress={() => navigation.navigate("card")}
        size={isFocused == "card" ? 70 : 50}
        backgroundColor="0"
        color={isFocused == "card" ? "black" : "white"}
        style={styles.navIcon}
      /> */}
      <Pressable
        onPress={() => navigation.navigate("card")}
        style={styles.navIcon}
      >
        {isFocused == "card" ? (
          <ColoredCoffeeCardIconSVG
            size={isFocused == "card" ? 70 : 50}
            backgroundColor="0"
            style={styles.navIcon}
            width={100}
            height={100}
          />
        ) : (
          <BlackCoffeeCardIconSVG
            width={100}
            height={100}
            fill="currentColor"
            style={styles.navIcon}
          />
        )}
      </Pressable>
      {/* <MaterialIcons.Button
        name="storefront"
        onPress={() => navigation.navigate("stores")}
        backgroundColor="0"
        size={isFocused == "shop" ? 70 : 50}
        color={isFocused == "shop" ? "black" : "white"}
        style={
          isFocused == "shop" ? styles.activeNav : { backgroundColor: "0" }
        }
      /> */}
    </View>
  );
}
const styles = StyleSheet.create({
  navbar: {
    backgroundColor: "#d3d3d3",
    height: "10%",
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "100%",
    borderTopLeftRadius: 40,
    borderTopRightRadius: 40,
    // marginTop: 40,
    // borderRadius: 40,
    // margin: 10,
    // borderWidth: 4,
    borderBottomWidth: 0,
    alignSelf: "flex-end",
    overflow: "visible",
  },
  navIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
  },
});
