import { View, Image } from "react-native";

const getCoffeesEarnt = () => {
  return 1;
};

function CoffeeTicker(checked) {
  return (
    <View style={{ width: 50, height: 50, backgroundColor: "cream" }}>
      {i >= coffeesEarnt ? (
        <Image source={require("../assets/bean_stamp.png")} />
      ) : null}
    </View>
  );
}

export default function LoyaltyCard({ navigation, route }) {
  console.log("LoyaltyCard");
  const cardDetails = route.params;
  const coffeesEarnt = getCoffeesEarnt();
  console.log(cardDetails);
  return (
    <View
      style={{
        minHeight: Dimensions.get("window").height,
        maxHeight: Dimensions.get("window").height,
        width: "100%",
        // backgroundColor: "#fff",
        flexDirection: "column",
        rowGap: 50,
        justifyContent: "space-between",
      }}
    >
      <Image
        source={require("../assets/" + cardDetails.logo)}
        width={100}
        height={100}
      />
      <View>
        {[...Array(cardDetails.pointTotal)].map((e, i) => {
          <CoffeeTicker i={i} n={coffeesEarnt} />;
        })}
      </View>
    </View>
  );
}
