import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, Image } from "react-native";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import { Dimensions } from "react-native";
import Feather from "react-native-vector-icons/Feather";
import * as React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

const Tab = createBottomTabNavigator();

function MainScreen() {
  return (
    <View>
      <Text>Main Screen</Text>
    </View>
  );
}

function CardScreen() {
  return (
    <View>
      <Text>card Screen</Text>
    </View>
  );
}
function ShopScreen() {
  return (
    <View>
      <Text>shop Screen</Text>
    </View>
  );
}

function NavBar() {
  return (
    <View style={styles.navbar}>
      <MaterialIcons.Button name="qr-code"></MaterialIcons.Button>
      <Image
        source={require("./assets/card_icon.png")}
        style={styles.icon}
      ></Image>
      <MaterialIcons.Button name="storefront"></MaterialIcons.Button>
      <FontAwesome.Button name="user-circle"></FontAwesome.Button>
    </View>
  );
}

function CoffeeSaved() {
  let coffeeSaves = 1;
  let cashSave = coffeeSaves * 4.5;
  return (
    <View style={styles.coffeesaved}>
      <Text>Your Free Coffee Counter</Text>
      <Text>
        {coffeeSaves}
        <Feather name="coffee" style={styles.icon}></Feather>
      </Text>
      {/* <Text>{`$${cashSave}`}</Text> */}
    </View>
  );
}

export default function App() {
  const name = "Julian Lund";
  return (
    <View style={styles.maincontainer}>
      <View style={styles.greetings}>
        <Text>Hello,</Text>
        <Text>{name}</Text>
        <Image source={require("./assets/coffee.png")} style={styles.image} />
      </View>
      <StatusBar style="auto" />
      <CoffeeSaved />
      <View style={styles.qr}>
        <Image
          source={require("./assets/QR_code_for_mobile_English_Wikipedia.svg")}
          style={{ height: "90%", width: "100%", resizeMode: "contain" }}
        />
      </View>
      <NavBar />
    </View>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    height: Dimensions.get("window").height,
    width: "100%",
    backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 10,
  },

  image: {
    width: 100,
    height: 100,
    borderRadius: 18,
  },
  icon: {
    width: 40,
    height: 40,
  },
  greetings: {
    flex: 2,
    backgroundColor: "red",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: "green",
    borderRadius: 44,
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 50,
    marginVertical: 15,
  },
  qr: {
    flex: 2,
    borderRadius: 30,
    width: "50%",
    backgroundColor: "orange",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
  },
  navbar: {
    backgroundColor: "blue",
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "auto",
    borderRadius: 44,
    marginHorizontal: 20,
    marginVertical: 10,
  },
});
