import { StyleSheet, View, Text } from "react-native";
import { Dimensions } from "react-native";
import { useIsFocused } from "@react-navigation/native";
import NavBar from "../components/NavBar";
import { useState, useEffect } from "react";
import QRCode from "react-native-qrcode-svg";
// import { useFonts, TitanOne_400Regular } from "@expo-google-fonts/titan-one";
import { getUserInfo } from "../firebasefunctions.js";
import { UserButton } from "../components/buttons.js";
import { CupTop } from "../assets/socialSVG.js";
import { LoadingScreen } from "./Loading.js";
import { auth } from "../firebase";
import { EnterName } from "./name.js";
import { useFonts } from "expo-font";

export default function Home({ navigation }) {
  console.log("Home Page");

  const [userDetails, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userCards, setUserCards] = useState([]);
  // const [hasDisplayName, setHasDisplayName] = useState(
  //   auth.currentUser.displayName == null ? false : true
  // );
  const isFocused = useIsFocused();
  let greetings = ["Hello", "Welcome back", "Hey ðŸ‘‹", "Good Morning"];
  let randomGreeting = greetings[Math.floor(Math.random() * greetings.length)];
  let [fontsLoaded] = useFonts({
    "IndieFlower-Regular": require("../assets/fonts/IndieFlower-Regular.ttf"),
    "Pacifico-Regular": require("../assets/fonts/Pacifico-Regular.ttf"),
    "TitanOne-Regular": require("../assets/fonts/TitanOne-Regular.ttf"),
  });

  // if (!hasDisplayName) {
  //   return <EnterName setHasDisplayName={() => setHasDisplayName(true)} />;
  // }
  // window.navigator.userAgent = "ReactNative";
  useEffect(() => {
    async function fetchFirebaseInfo() {
      if (userDetails === null) {
        console.log("getting user info");
        const { data, cards } = await getUserInfo();
        setUserCards(cards);
        setUserInfo(data);
        setLoading(false);
      }
    }
    fetchFirebaseInfo();
  }, [userDetails]);

  if (!fontsLoaded) {
    return null;
  }

  if (loading) {
    return <LoadingScreen />;
  }
  return (
    <View style={styles.maincontainer}>
      {/* <ImageBackground
        source={require("../assets/background.png")}
        resizeMode="cover"
        style={styles.image}
      > */}
      <View style={[styles.greetings]}>
        <Text
          style={{
            fontSize: 36,
            fontFamily: "TitanOne-Regular",
          }}
        >
          {randomGreeting}
          {"\n"}
          <Text
            style={{ fontWeight: "bold", fontSize: 46 }}
            numberOfLines={1}
            adjustsFontSizeToFit={true}
          >
            {userDetails.name.split(" ")[0]}
          </Text>
        </Text>
        <UserButton />
      </View>
      {/* <CoffeeSaved coffeenumber={userDetails.freeCoffees} /> */}
      {/* <CoffeeSaved coffeenumber={userDetails.coffee_earnt} /> */}
      <View style={[styles.qr]}>
        <Text style={{ fontSize: 50, fontFamily: "Pacifico-Regular" }}>
          Scan Here
        </Text>
        <View
          style={{ backgroundColor: "#eaded6", padding: 20, borderRadius: 20 }}
        >
          <QRCode
            value={userDetails.userId}
            size={Dimensions.get("window").height * 0.3}
            backgroundColor={"transparent"}
            logo={require("../assets/rect2.png")}
            logoSize={Dimensions.get("window").height * 0.15}
          />
        </View>
      </View>
      <NavBar
        navigation={navigation}
        isFocused={isFocused ? "main" : null}
        cards={userCards}
      />
      {/* </ImageBackground> */}
    </View>
  );
}

function CoffeeSaved({ coffeenumber }) {
  return (
    <View style={[styles.coffeesaved]}>
      <Text
        style={{
          fontSize: 35,
          paddingLeft: 20,
          fontWeight: "bold",
          textAlign: "center",
        }}
      >
        Free Coffees
      </Text>
      <View style={{ alignItems: "center", justifyContent: "center" }}>
        <CupTop width={90} height={90}></CupTop>
        <Text
          style={{
            fontSize: 40,
            fontWeight: "bold",
            position: "absolute",
            right: "42%",
            color: "#eaded6",
          }}
        >
          {coffeenumber}
        </Text>
      </View>
    </View>
  );
}
const colors = {
  bg: "#fff8e7",
  widgetbg: "rgba(205, 184, 145, 0.90))",
};

const styles = StyleSheet.create({
  image: {
    flex: 1,
    justifyContent: "center",
    minHeight: Dimensions.get("window").height,
    maxHeight: Dimensions.get("window").height,
    width: "100%",
    flexDirection: "column",
    rowGap: 50,
    justifyContent: "space-between",
    overflow: "hidden",
  },
  maincontainer: {
    // minHeight: Dimensions.get("window").height,
    // maxHeight: Dimensions.get("window").height,
    flex: 1,
    width: "100%",
    // backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 30,
    justifyContent: "space-between",
    overflow: "hidden",
    backgroundColor: "#936748",
  },
  greetings: {
    flex: 1.5,
    paddingHorizontal: 30,
    paddingBottom: 20,
    backgroundColor: colors.widgetbg,
    borderBottomLeftRadius: 20,
    borderBottomRightRadius: 20,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
    alignItems: "flex-end",
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: colors.widgetbg,
    borderRadius: 44,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "65%",
    alignSelf: "center",
  },
  qr: {
    flex: 5,
    width: "auto",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    borderRadius: 20,
    rowGap: 10,
    backgroundColor: colors.widgetbg,
    padding: 20,
  },
  coffeesaved: {
    flex: 1.5,
    backgroundColor: colors.widgetbg,
    borderRadius: 20,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "65%",
    alignSelf: "center",
    padding: 10,
  },
});
