import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { app } from "../firebase";

// Replace the uri string with your MongoDB deployment's connection string.
const db = getFirestore(app);

const uri =
  "mongodb+srv://julianlund22:9Nyqlonx46Vfin9s@loyalcoffee.gr6rlnl.mongodb.net/?retryWrites=true&w=majority";

const client = new MongoClient(uri);

async function run({ choice, user }) {
  try {
    await client.connect();
    // database and collection code goes here
    // insert code goes here
    // display the results of your operation
    await client.db("LoyalCoffee").command({ ping: 1 });
    switch (choice) {
      case "newUser":
        const result = await client
          .db("LoyalCoffee")
          .collection("Users")
          .insertOne(user);
        console.log(
          `New user created with the following id: ${result.insertedId}`
        );
        break;
      case "getUser":
        getUser();
        break;
      case "updateUser":
        updateUser();
        break;
      case "deleteUser":
        deleteUser();
        break;
      case "getUsersCards":
        getUsersCards();
        break;
      case "getShops":
        GetShops();
        break;
    }
    console.log(
      "Pinged your deployment. You successfully connected to MongoDB!"
    );
  } finally {
    // Ensures that the client will close when you finish/error

    await client.close();
  }
}

run().catch(console.dir);

