import {
  View,
  Image,
  Text,
  StyleSheet,
  TextInput,
  Pressable,
  Dimensions,
  ActivityIndicator,
  ScrollView,
  Linking,
} from "react-native";
import NavBar from "../components/NavBar";
import { useIsFocused } from "@react-navigation/native";
import * as Location from "expo-location";
import { useState } from "react";
import { UserButton } from "../components/buttons";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import { getUserCards, getStoreInfo, getStoreLogo } from "../firebasefunctions";
import { useEffect } from "react";
import { CoffeeCupIcon } from "../assets/socialSVG";
import { getDistanceFromLatLonInKm, geocode } from "../components/location";
export default function CardScreen({ navigation }) {
  console.log("Card page");
  const isFocused = useIsFocused();
  const [cards, setCards] = useState(null);
  const [searchStoreFilter, setSearchFilter] = useState("");
  const [location, setLocation] = useState();

  // useEffect(() => {
  //   const getPermissions = async () => {
  //     let { status } = await Location.requestForegroundPermissionsAsync();
  //     if (status !== "granted") {
  //       console.log("presmission not granted");
  //       return;
  //     }

  //     let currentLocation = await Location.getCurrentPositionAsync({});
  //     setLocation(currentLocation);
  //     console.log("Location:");
  //     console.log(currentLocation);
  //   };
  //   getPermissions();
  // }, []);

  useEffect(() => {
    async function fetchCards() {
      try {
        const data = await getUserCards();
        setCards(data);
      } catch (error) {
        // add error handling here
        console.log(error);
      }
    }

    fetchCards();
  }, []);
  console.log(cards);
  const Loading = () => {
    return (
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <Text>Getting Your Cards </Text>
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  };

  const filterList = (list) => {
    console.log("filter");

    console.log(list);
    return list.filter((card) =>
      card.name.toLowerCase().includes(searchStoreFilter.toLowerCase())
    );
    // return Array(10).fill(list[0]);
  };

  return (
    <View style={styles.maincontainer}>
      <View>
        <View style={styles.greetings}>
          <Text style={{ fontSize: 36, fontWeight: "normal" }}>Your Cards</Text>
          <UserButton />
        </View>
        <TextInput
          style={{
            borderRadius: 25,
            borderWidth: 5,
            borderColor: "#d3d3d3",
            width: "auto",
            backgroundColor: "#fff",
            marginHorizontal: 20,
            marginTop: 10,
            marginBottom: 5,
            padding: 10,
            paddingHorizontal: 20,
          }}
          placeholder="Find Store"
          onChangeText={(newVal) => setSearchFilter(newVal)}
        ></TextInput>
        <ScrollView style={styles.cardsContainer}>
          {cards === null ? (
            <Loading />
          ) : (
            filterList(cards).map((card, index) => (
              <CardWidget
                card={card}
                key={index}
                navigation={navigation}
                filter={searchStoreFilter}
                // location={location}
              />
            ))
          )}{" "}
          {/* <View style={{ height: 500 }}></View> */}
        </ScrollView>
      </View>
      <NavBar navigation={navigation} isFocused={isFocused ? "card" : null} />
    </View>
  );
}

function CardWidget({ navigation, card, location }) {
  // const getDistance = (lat1, lon1, lat2, lon2) => {
  //   return 0;
  // };
  // useEffect(() => {
  //   async function getDistance() {
  //     console.log(card.location);
  //     let storeLocation = await geocode(card.location);
  //     console.log(storeLocation);
  //     let distance = getDistanceFromLatLonInKm(
  //       location.latitude,
  //       location.longitude,
  //       storeLocation.latitude,
  //       storeLocation.longitude
  //     );
  //     console.log(distance);
  //   }
  //   getDistance();
  // }, []);

  const navigateLoyaltyPage = (card) => {
    console.log("push: " + card);
    navigation.push("loyaltyCard", card);
  };

  // const Loading = () => {
  //   return (
  //     <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
  //       <Text>Getting Store Info </Text>
  //       <ActivityIndicator size="large" color="#0000ff" />
  //     </View>
  //   );
  // };

  return (
    <Pressable
      style={{
        flexDirection: "row",
        borderRadius: 25,
        borderWidth: 5,
        borderColor: "#d3d3d3",
        width: "100%",
        backgroundColor: "#fff",
        minHeight: 100,
        alignItems: "center",
        padding: 20,
        marginVertical: 5,
      }}
      onPress={() => {
        navigateLoyaltyPage(card);
      }}
    >
      {/* {loading ? (
        <Loading />
      ) : ( */}

      <Image
        style={{
          width: 60,
          height: 60,
          loading: "lazy",
        }}
        source={{
          uri: card.logo,
        }}
      />
      <View
        style={{
          justifyContent: "center",
          width: "60%",
          paddingHorizontal: 20,
        }}
      >
        <Text
          style={{
            fontWeight: 40,
            fontSize: 18,
            fontWeight: "bold",
            textTransform: "uppercase",
          }}
        >
          {card.name}
        </Text>
        <Text
          style={{ fontSize: 15, textDecorationLine: "underline" }}
          onPress={() => {
            Linking.openURL(
              `https://www.google.com/maps/place/${card.location.replace(
                " ",
                "+"
              )}`
            );
          }}
        >
          {card.location}{" "}
        </Text>
        {/* <Text>{getDistance}Away</Text> */}
      </View>

      {/* TODO add cup icon that filled based on amount of coffees on loyalty card */}
      <View
        style={{
          height: "100%",
          justifyContent: "center",
          padding: "auto",
        }}
      >
        <CoffeeCupIcon width={70} height={70} fill="transparent" />
        <View
          style={{
            position: "absolute",
            borderBottomLeftRadius: 20,
            borderBottomRightRadius: 20,
            backgroundColor: "#583927",
            height: 35 * (card.coffees_purchased / card.coffees_required),
            width: 40,
            bottom: "15%",
            right: "25%",
            zIndex: -1,
            elevation: -1,
          }}
        ></View>
      </View>
    </Pressable>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    height: Dimensions.get("window").height,
    width: "100%",
    // backgroundColor: "#fff",
    flexDirection: "column",
    justifyContent: "space-between",
    flex: 1,
    overflow: "hidden",
  },
  cardsContainer: {
    alignSelf: "stretch",
    paddingHorizontal: 20,
    height: 500,
  },
  greetings: {
    flex: 1,
    backgroundColor: "#d3d3d3",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
    alignItems: "flex-end",
    padding: 40,
  },
  cards: {
    flexDirection: "row",
    borderRadius: 25,
    borderWidth: 5,
    borderColor: "#d3d3d3",
    width: "100%",
    backgroundColor: "#fff",
  },
  cardpoints: {},
  pointsTotal: {},
  cardname: {
    fontWeight: 50,
  },
  cards: {},
});
