import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, Image, Button, Pressable } from "react-native";
import { Dimensions } from "react-native";
import * as React from "react";
import { NavigationContainer, useIsFocused } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { SignInScreen, SignUpScreen } from "./SignIn";
import AuthStack from "./AuthStack";

const Stack = createNativeStackNavigator();

const IsSignedIn = () => {
  // custom logic
  return false;
};

export default function App() {
  const isSignedIn = IsSignedIn();
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        {isSignedIn ? (
          <>
            <Stack.Screen
              name="Home"
              component={MainScreen}
              options={{
                headerStyle: {
                  opacity: 0,
                  height: 0,
                },
                animation: "slide_from_left",
              }}
            />
            <Stack.Screen
              name="card"
              component={CardScreen}
              options={{
                headerStyle: {},
                animation: "slide_from_left",
              }}
            />
            <Stack.Screen
              name="stores"
              component={StoresScreen}
              options={{
                headerStyle: {
                  opacity: 0,
                  height: 0,
                },
                animation: "slide_from_left",
              }}
            />
            <Stack.Screen
              name="settings"
              component={SettingsScreen}
              options={{
                headerStyle: {
                  opacity: 0,
                  height: 0,
                },
              }}
            />
          </>
        ) : (
          <AuthStack />
        )}
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  maincontainer: {
    minHeight: Dimensions.get("window").height,
    maxHeight: Dimensions.get("window").height,
    width: "100%",
    backgroundColor: "#fff",
    flexDirection: "column",
    rowGap: 50,
    justifyContent: "space-between",
  },

  image: {
    width: 100,
    height: 100,
    borderRadius: 18,
  },
  icon: {
    width: 40,
    height: 40,
  },
  greetings: {
    flex: 1.25,
    backgroundColor: "#d3d3d3",
    borderBottomLeftRadius: 40,
    borderBottomRightRadius: 40,
    flexDirection: "row",
    width: "100%",
    justifyContent: "space-between",
    alignItems: "flex-end",
    padding: 40,
  },
  coffeesaved: {
    flex: 1,
    backgroundColor: "#d3d3d3",
    borderRadius: 44,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "65%",
    alignSelf: "center",
  },
  qr: {
    flex: 3,
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
  },
  navbar: {
    backgroundColor: "#d3d3d3",
    flex: 0.75,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    width: "100%",
    borderTopLeftRadius: 40,
    borderTopRightRadius: 40,
    // marginTop: 40,
    // borderRadius: 40,
    // margin: 10,
    // borderWidth: 4,
    borderBottomWidth: 0,
    alignSelf: "flex-end",
    overflow: "visible",
  },
  button: {
    borderRadius: 50,
    backgroundColor: "0",
    borderWidth: 2,
    justifyContent: "center",
    alignSelf: "center",
  },
  navIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
  },
  activeNav: {
    borderRadius: 30,
    borderWidth: 5,
    backgroundColor: "brown",
    padding: 5,
    textAlign: "center",
  },
});
